import $ from 'jquery';
import { graphRenderer } from './renderer';

export const renderResult = (selector, data) => {
  const d3Graph = graphRenderer();
  try {
    if (data) {
      let div = $(`#${selector}`);
      div.empty();
      d3Graph.render(selector, div, data);
    } else {
      if (err) {
        window.alert(err.length > 0
          ? `Cypher error:\n${err}`
          : `JS Error:\n${err}`);
      }
    }
  } catch (e) {
    window.alert(`Caught error: ${e}`);
  }
};

export default { renderResult };
